import { Grid } from '@material-ui/core';
import taskImage from '../../images/extraVolunteer.png';
import React from 'react';
import './Task.css';

const Task = (props) => {
    const { date, event } = props.eventData;
    return (
        <div>
            {/* <p>event{event}</p>
            <p>date{date}</p> */}
            <Grid container item xs={11} spacing='5' style={{ margin: 'auto' }}>
                <Grid container item xs={10} sm={5} justify='space-around'
                    style={{ boxShadow: '0px 2px 5px lightGray', borderRadius: '10px', width: '100%', margin: '10px' }}>
                    <Grid item xs={10} md={5} style={{ width: '50%' }}>
                        <img style={{ width: '100%', height: '180px' }} src={taskImage} alt="" />
                    </Grid>
                    <Grid item xs={10} md={5} style={{ width: '50%', marginLeft: '15px' }}>
                        <h3>{event}</h3>
                        <h4>{date}</h4>
                        <div style={{ textAlign: 'right', marginTop: '30px' }}>
                            <button className='event-cancel'>
                                cancel
                                    </button>
                        </div>
                    </Grid>
                </Grid>
            </Grid>
        </div>
    );
};

export default Task;
